<button id="buttonAction" onclick="window.location.href = '?page=documents/event/eventCalendar.php'">kalendarz ze zdarzeniami</button><br/><?phpshowLocalEvents($_SESSION['Id_User']);showTeamEvents($_SESSION['Id_User']);?><?phpfunction showLocalEvents($idUser){ $query="SELECT Event.*, Event_User.Id_User FROM Event  INNER JOIN Event_User ON Event.Id_Event=Event_User.Id_Event WHERE Event_User.Id_User=$idUser "; $result = mysql_query($query) or die('Error (query)');   echo '---------<br/>Nadchodzace wydarzenia (lokalne):';   if(mysql_num_rows($result) > 0) {     while ($r = mysql_fetch_object($result)) {	echo "<div id='boxEvent'>";		echo "<button onclick='window.location.href = \"?page=documents/event/oneEvent.php&idEvent=$r->Id_Event\"'>";	echo "---". $r->Name.'---';	$fullDate=$r->Year.'-'.$r->Month.'-'.$r->Day;	$fullHour=$r->Hour.':'.$r->Minute;	echo "<span class='title'>Kiedy: </span>". $fullDate.', godz '.$fullHour.'---';echo '	</button>';	echo '</div>';	}}}function showTeamEvents($idUser){ $query="SELECT Event.* FROM Event  INNER JOIN Event_Team ON Event.Id_Event=Event_Team.Id_Event INNER JOIN Team ON Team.Id_Team=Event_Team.Id_Team INNER JOIN User_Team ON User_Team.Id_Team=Team.Id_Team WHERE User_Team.Id_User=$idUser "; $result = mysql_query($query) or die('Error (query)');   echo '---------<br/>Nadchodzace wydarzenia (druzynowe):';   if(mysql_num_rows($result) > 0) {     while ($r = mysql_fetch_object($result)) {	echo "<div id='boxEvent'>";		echo "<button onclick='window.location.href = \"?page=documents/event/oneEvent.php&idEvent=$r->Id_Event\"'>";	echo "---". $r->Name.'---';	$fullDate=$r->Year.'-'.$r->Month.'-'.$r->Day;	$fullHour=$r->Hour.':'.$r->Minute;	echo "<span class='title'>Kiedy: </span>". $fullDate.', godz '.$fullHour.'---';	echo '	</button>';	echo '</div>';	}}}?>